import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { db } from "../firebase";
import { collection, getDocs, getDoc, doc, query, orderBy, where } from "firebase/firestore";
import { jsPDF } from "jspdf";
import autoTable from "jspdf-autotable";
import "./MusteriDetay.css";
import { useNavigate } from "react-router-dom";


const MusteriDetay = () => {
    const { id } = useParams();
    const [musteri, setMusteri] = useState(null);
    const [hareketler, setHareketler] = useState([]);
    const [loading, setLoading] = useState(true);
const navigate = useNavigate();



const escaFoodLogoBase64 = "data:image/png;base64,";

let bakiye = 0; 

    useEffect(() => {
        const fetchData = async () => {
            try {
                const docRef = doc(db, "musteriler", id);
                const docSnap = await getDoc(docRef);

                if (docSnap.exists()) {
                    setMusteri({ id: docSnap.id, ...docSnap.data() });
                } else {
                    console.log(`Müşteri ID (${id}) Firestore'da bulunamadı.`);
                }

                const tekliflerQuery = query(
                    collection(db, "teklifler"),
                    where("musteriId", "==", id),
                    orderBy("tarih")
                );

                const tahsilatlarQuery = query(
                    collection(db, "tahsilatlar"),
                    where("musteriId", "==", id),
                    orderBy("tarih")
                );

                const [teklifSnapshot, tahsilatSnapshot] = await Promise.all([
                    getDocs(tekliflerQuery),
                    getDocs(tahsilatlarQuery),
                ]);

                const teklifData = teklifSnapshot.docs
                    .map((doc) => ({ id: doc.id, type: "teklif", ...doc.data() }))
                    .filter(teklif => !teklif.iptal); // İptal edilen teklifler hariç tutuldu

                const tahsilatData = tahsilatSnapshot.docs.map((doc) => ({
                    id: doc.id,
                    type: "tahsilat",
                    ...doc.data(),
                }));

                const combinedData = [...teklifData, ...tahsilatData].sort(
                    (a, b) => a.tarih - b.tarih
                );

                setHareketler(combinedData);
                setLoading(false);
            } catch (error) {
                console.error("Veri alınırken hata oluştu:", error);
            }
        };

        fetchData();
    }, [id]);

    if (loading) return <p>Yükleniyor...</p>;
    if (!musteri) return <p className="text-red-500 font-bold">Müşteri bulunamadı!</p>;
const maxWidth = 60;
  const formatCurrency = (value) => {
    return new Intl.NumberFormat("tr-TR", { minimumFractionDigits: 2, maximumFractionDigits: 2 }).format(value);

  };


const bakiyeRenk = bakiye >= 0 ? [0, 128, 0] : [255, 0, 0]; // [R, G, B] formatında renkler

const cleanText = (text) => {
  if (!text) return ""; // Eğer text boşsa, hata almamak için boş string döndür

  const turkishMap = {
    "Ç": "C", "Ğ": "G", "İ": "I", "Ö": "O", "Ş": "S", "Ü": "U",
    "ç": "c", "ğ": "g", "ı": "i", "ö": "o", "ş": "s", "ü": "u"
  };

  return text
    .replace(/[\^'`"]/g, "") // ^, ', ` ve " işaretlerini kaldırır
    .replace(/-/g, " ") // - işaretini boşluk ile değiştirir
    .replace(/\s+/g, " ") // Fazla boşlukları tek boşluğa çevirir
    .trim() // Baştaki ve sondaki gereksiz boşlukları temizler
    .split("") // String'i karakterlerine ayır
    .map(char => turkishMap[char] || char) // Türkçe karakterleri İngilizce eşdeğeri ile değiştir
    .join(""); // Karakterleri tekrar birleştir
};

    const generatePDF = () => {
        const doc = new jsPDF();
        doc.setFont("helvetica", "normal");
  // **EscaFood Logosu**
  doc.addImage(escaFoodLogoBase64, "PNG", 10, 10, 40, 30);
 doc.setFont("helvetica", "bold");
doc.setFontSize(21);
        doc.text("Hesap Ekstresi", 80, 30);

 doc.setFont("helvetica", "bold");
  doc.setFontSize(10);    
doc.text(cleanText("ESCA FOOD GIDA DIS TICARET"), 14, 55);
doc.text(cleanText("SANAYI ANONIM SIRKETI"), 14, 60);

  doc.setFont("helvetica", "normal");
doc.setFontSize(10);  
  doc.text(cleanText("Yeni Baglica Mah Etimesgut Blv. No: 6H/A"), 14, 65);
  doc.text(cleanText("Etimesgut, ANKARA – Türkiye"), 14, 70);
  doc.text(cleanText("Vergi No: 3770983099 (Etimesgut)"), 14, 75);


  doc.setFontSize(10);
doc.setFont("helvetica", "bold");
    doc.text(`Sayin;`, 130, 55);
doc.text(`Adres:`, 130, 85);
doc.setFont("helvetica", "normal");

const maxWidth = 60; // ✅ Metnin genişliği ayarlanabilir
const musteriAdiWrapped = doc.splitTextToSize(cleanText(musteri.musteriAdi), maxWidth);
  doc.setFont("helvetica", "normal");
doc.setFontSize(10);
doc.text(musteriAdiWrapped, 130, 60);
doc.text(cleanText(`${musteri.adres}`), 145, 85);

// ✅ `bakiye` değişkenini en başta tanımla (0 olarak başlat)
 

let toplamTahsilatlar = hareketler
    .filter(h => h.type === "tahsilat" && !h.iptal)  // ✅ İptal olmayanları al
    .reduce((sum, h) => sum + parseFloat(h.tahsilatTutari || 0), 0);

let toplamTeklifler = hareketler
    .filter(h => h.type === "teklif" && !h.iptal)  // ✅ İptal olmayanları al
    .reduce((sum, h) => sum + parseFloat(h.geneltoplamTutar || 0), 0);

let cariHesap = parseFloat(musteri.carihesap || 0);

// ✅ `bakiye` artık iptal edilen işlemleri içermiyor
bakiye = -toplamTahsilatlar + toplamTeklifler + cariHesap;

let finalY = 95;
let hareketListesi = [
    ["09.03.2025", "", "Açilis Bakiyesi", "", `${bakiye.toLocaleString("tr-TR", { minimumFractionDigits: 2 })} TL`]
];

hareketler
  .filter(hareket => !hareket.iptal) // İptal edilenleri filtreledik
  .forEach(hareket => {
    let tutar = parseFloat(hareket.type === "teklif" ? hareket.geneltoplamTutar : hareket.tahsilatTutari);
    let tutarRenk = hareket.type === "teklif" ? "red" : "green";

    if (hareket.type === "teklif") {
        bakiye -= tutar;
    } else {
        bakiye += tutar;
    }

    let bakiyeRenk = bakiye >= 0 ? "green" : "red";

    hareketListesi.push([
        new Date(hareket.tarih).toLocaleDateString("tr-TR"),
        hareket.type === "teklif" ? hareket.teklifNo : (hareket.tahsilatNo || "-"),
        hareket.type === "teklif" ? "Teklif" : "Tahsilat",
        { content: `${tutar.toLocaleString("tr-TR", { minimumFractionDigits: 2 })} TL`, styles: { textColor: tutarRenk } },
        { content: `${bakiye.toLocaleString("tr-TR", { minimumFractionDigits: 2 })} TL`, styles: { textColor: bakiyeRenk } }
    ]);
});

// ✅ `bakiyeRenk` değişkeni burada tanımlanmalı
let bakiyeRenk = bakiye >= 0 ? [0, 128, 0] : [255, 0, 0];

autoTable(doc, {
    startY: finalY,
    head: [["Tarih", "Belge No", "Tür", "Tutar", "Bakiye"]],
    body: hareketListesi,
    styles: { font: "helvetica", fontSize: 10 },
    headStyles: { fillColor: [41, 128, 185], textColor: [255, 255, 255] },
    alternateRowStyles: { fillColor: [240, 240, 240] }
});


const today = new Date();
const formattedDate = `${today.getDate()}-${today.getMonth() + 1}-${today.getFullYear()}`;

doc.text(`${formattedDate} Tarihli Cari Hesap Bakiyesi:`, 15, doc.lastAutoTable.finalY + 10); // ✅ Pozisyon ayarlandı
doc.setTextColor(...bakiyeRenk);
doc.text(
    `${bakiye.toLocaleString("tr-TR", { minimumFractionDigits: 2 })} TL`,
    90,
    doc.lastAutoTable.finalY + 10
);

    // 📌 PDF dosya adını müşteri adı + tarih olacak şekilde güncelle
    doc.save(`HesapEkstresi_${musteri.musteriAdi}_${formattedDate}.pdf`);
};
   

    return (
    <div className="musteri-detay">
      <h2 className="title">{musteri.musteriAdi} - Detayları</h2>
      <p><strong>Adres:</strong> {musteri.adres}</p>
      <p><strong>Cari Hesap:</strong> ₺ {parseFloat(musteri.carihesap || 0).toLocaleString("tr-TR", { minimumFractionDigits: 2 })}</p>

      <h3 className="subtitle">Hesap Ekstresi</h3>
      <button className="pdf-button" onClick={generatePDF}>PDF İndir</button>

      <h3 className="subtitle">Müşteri Hareketleri</h3>
      <table className="styled-table">
        <thead>
          <tr>
            <th>Tarih</th>
            <th>Belge No</th>
            <th>Tür</th>
            <th>Tutar</th>
            <th>Detay</th>
          </tr>
        </thead>
<tbody>
  {hareketler.length > 0 ? (
    hareketler.map((hareket) => (
      <tr
        key={hareket.id}
        className={hareket.iptal ? "cancelled" : ""}
        style={{
          textDecoration: hareket.iptal ? "line-through" : "none",
          opacity: hareket.iptal ? 0.6 : 1, 
        }}
      >
        <td>{new Date(hareket.tarih).toLocaleDateString("tr-TR")}</td>
        <td>{hareket.type === "teklif" ? hareket.teklifNo : hareket.tahsilatNo}</td>
        <td>{hareket.type === "teklif" ? "Teklif" : "Tahsilat"}</td>
        <td>
          ₺{parseFloat(
            hareket.type === "teklif" ? hareket.geneltoplamTutar : hareket.tahsilatTutari
          ).toLocaleString("tr-TR", { minimumFractionDigits: 2 })}
        </td>
        <td>
          {hareket.type === "teklif" ? (
            <button onClick={() => navigate(`/teklifler/detay/${hareket.id}`)}>Detay</button>
          ) : (
            <button onClick={() => navigate(`/tahsilatlar/detay/${hareket.id}`)}>Detay</button>
          )}
        </td>
      </tr>
    ))
  ) : (
    <tr><td colSpan="5" className="text-center">Hareket bulunamadı.</td></tr>
  )}
</tbody>
      </table>
    </div>
  );
}

export default MusteriDetay;
